mapper.select:
select count(1) FROM xy_article 

	AND ID = #{id}
	AND instr(TITLE ,#{title})>0
	AND instr(CONTENT,#{content})>0
	AND USER_ID = #{userId}
	AND CREATE_TIME = #{createTime}
	AND UPDATE_TIME = #{updateTime}
	AND TYPE_ID = #{typeId}
	AND KEY_WORD = #{keyWord}
	AND PRODUCE1 = #{produce1}
	AND READ_COUNT = #{readCount}
	AND COMMENT_COUNT = #{commentCount}
	AND PRODUCE2 = #{produce2}
	AND IMG_URL = #{imgUrl}

SELECT

FROM xy_article

	AND ID = #{id}
	AND TITLE = #{title}
	AND CONTENT = #{content}
	AND USER_ID = #{userId}
	AND CREATE_TIME = #{createTime}
	AND UPDATE_TIME = #{updateTime}
	AND TYPE_ID = #{typeId}
	AND KEY_WORD = #{keyWord}
	AND PRODUCE1 = #{produce1}
	AND READ_COUNT = #{readCount}
	AND COMMENT_COUNT = #{commentCount}
	AND PRODUCE2 = #{produce2}
	AND IMG_URL = #{imgUrl}

SELECT
article.ID as "id"
, article.TITLE as "title"
, article.CONTENT as "content"
, article.USER_ID as "userId"
, article.CREATE_TIME as "createTime"
, article.UPDATE_TIME as "updateTime"
, article.TYPE_ID as "typeId"
, atype.NAME as "typeName"
, article.KEY_WORD as "keyWord"
, article.PRODUCE1 as "produce1"
, article.READ_COUNT as "readCount"
, article.COMMENT_COUNT as "commentCount"
, article.PRODUCE2 as "produce2"
, article.IMG_URL as "imgUrl"
FROM xy_article article left join xy_article_type  atype on article.TYPE_ID=atype.id

	AND ID = #{id}
	AND instr(TITLE,#{title})>0
	AND CONTENT = #{content}
	AND USER_ID = #{userId}
	AND CREATE_TIME = #{createTime}
	AND UPDATE_TIME = #{updateTime}
	AND article.TYPE_ID = #{typeId}
	AND instr(atype.name,#{typeName})>0
	AND KEY_WORD = #{keyWord}
	AND PRODUCE1 = #{produce1}
	AND READ_COUNT = #{readCount}
	AND COMMENT_COUNT = #{commentCount}
	AND PRODUCE2 = #{produce2}
	AND IMG_URL = #{imgUrl}
