mapper.select:
SELECT

FROM xy_message

	AND ID = #{id}
	AND SOURCE = #{source}
	AND DEST = #{dest}
	AND TITLE = #{title}
	AND CONTENT = #{content}
	AND MERGE_ = #{merge}
	AND READ_ = #{read}
	AND CREATE_TIME = #{createTime}

SELECT sou.ID as "id",sou.CONTENT as "content", sou.CREATE_TIME as "createTime",u.user_name as "sourName",u.head as "sourceHead"
FROM xy_message sou LEFT JOIN xy_user u ON sou.source=u.id

	AND sou.ID = #{id}
	AND sou.SOURCE = #{source}
	AND sou.DEST = #{dest}
	AND sou.TITLE = #{title}
	AND sou.CONTENT = #{content}
	AND sou.MERGE_ = #{merge}
	AND sou.READ_ = #{read}
	AND sou.CREATE_TIME = #{createTime}

order by sou.CREATE_TIME desc

mapper.update:
UPDATE xy_message
ID = #{id}, SOURCE = #{source}, DEST = #{dest}, TITLE = #{title}, CONTENT = #{content}, READ_ = #{read} CREATE_TIME = #{createTime},
WHERE ID=#{id}

update.foreach:
UPDATE xy_message
ID = #{item.id}, SOURCE = #{item.source}, DEST = #{item.dest}, TITLE = #{item.title}, CONTENT = #{item.content}, READ_ = #{item.read}, CREATE_TIME =#{item.createTime},
WHERE ID=#{item.id}
